import React, { Component } from 'react';
import styles from './ItemModel.module.scss';

export class ItemModel extends Component {
  render() {
    const [open, setOpen] = React.useState(false);
    return (
      <div className={styles.itemModelStyles}>
        <img src={'./img/' + this.props.item.img} alt={'images'} />
        <h1>{this.props.item.title}</h1>
        <span onClick={() => setOpen(!open)}></span>
        <h2>
          <p>Опис товару</p>
          {this.props.item.text}
        </h2>
        <h2>
          <p>Характеристики</p>
          {this.props.item.characteristics}
        </h2>
        <h3>
          <p>Группа:</p>
          {this.props.item.category}
        </h3>
        <h1>{this.props.item.price}грн.</h1>
        <div className={styles.toCart}>+</div>
      </div>
    );
  }
}

export default ItemModel;
