import React from 'react';
import styles from './Sort.module.scss';

function Sort() {
  const [open, setOpen] = React.useState(false);

  return (
    <div className={styles.sort}>
      <div className={styles.sortLabel}>
        <img src={'./image/icon1.jog'} alt={'images'} />

        <span onClick={() => setOpen(!open)}>Опис товару</span>
      </div>
      {open && (
        <div className={styles.sort__popup}>
          <ul>
            <li className={styles.active}>
              'Отвертки Irwin Pro Comfort имеют высокие показатели долговечности и удобства. Эти
              отвертки имеют эргономичную трехгранную ручку со скругленными углами из двух
              материалов, магнитный наконечник с черным фосфатным покрытием и стержень с
              хромо-ванадиевой стали с коррозионной защитой. Шестигранная шейка оптимизирует
              передачу крутящего момента при использовании ключа.'
            </li>
          </ul>
        </div>
      )}
    </div>
  );
}

export default Sort;
