import React, { Component, useState } from 'react';
import styles from './ItemModel.module.scss';
import Modal from './Modal';

export class ItemModel extends Component {
  render() {
    const [open, setOpen] = useState(false);
    return (
      <div className={styles.itemModelStyles}>
        <img src={'./img/' + this.props.item.img} alt={'images'} />
        <h1>{this.props.item.title}</h1>

        <div>
          <div className="App-modal">
            <button onClick={() => setOpen(true)} className="open-modal-btn">
              ✨ Открыть окно
            </button>
            <Modal open={open} setOpen={setOpen}>
              <img src="https://c.tenor.com/2B3r0f7Vvg4AAAAd/%D1%83%D0%BA%D1%80%D0%B0%D1%97%D0%BD%D0%B0.gif" />
              <h3>Is okay!!!!</h3>
              <button className="button-modal">Serasch</button>
            </Modal>
          </div>
        </div>

        <h3>
          <p>Группа:</p>
          {this.props.item.category}
        </h3>
        <h1>{this.props.item.price}грн.</h1>
        <div className={styles.toCart}>+</div>
      </div>
    );
  }
}

export default ItemModel;
