import React, { Component, useState } from 'react';

import styles from './ItemModel.module.scss';

export class ItemModel extends Component {
  render() {
    const [open, setOpen] = useState(false);
    return (
      <div className={styles.itemModelStyles}>
        <img className={styles.imgItemModel} src={'./img/' + this.props.item.img} alt={'images'} />
        <h1>{this.props.item.title}</h1>

        <div className={styles.sort}>
          <div className={styles.sortLabel}>
            <span onClick={() => setOpen(!open)}>
              <img className={styles.imgIcon} src={'./img/icon1.png'} alt={'images'} />
              Опис товару
            </span>
          </div>
          {open && (
            <div className={styles.sort__popup}>
              <ul>
                <li className={styles.active}>{this.props.item.text}</li>
              </ul>
            </div>
          )}
        </div>

        <p>Характеристики</p>
        <h2 className={styles.openCharacteristics}>{this.props.item.characteristics}</h2>
        <h3>
          <p>Группа:</p>
          {this.props.item.category}
        </h3>
        <h1>{this.props.item.price}грн.</h1>
        <div className={styles.toCart}>+</div>
      </div>
    );
  }
}

export default ItemModel;
